package br.com.farmacristo.model.util.images;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

import org.springframework.web.multipart.MultipartFile;

import br.com.farmacristo.model.exception.specialization.InvalidFields;

public class ImageUtils {
	
	public static final String encryptedDefaultImage = "";
	public static final String encryptedDefaultUserImage ="";

	private static final List<String> VALID_EXTENSIONS = Arrays.asList("jpg", "jpeg", "png", "gif");
	
	public static void validation(MultipartFile file) throws InvalidFields {
		List<String> messages = new ArrayList<String>();
		ImageUtils.isImage(messages, file);
		
		if(!messages.isEmpty())
			throw new InvalidFields(messages, "Algumas informações foram inseridas de maneira incorreta. Por favor, verifique e corrija os campos necessários.");
	}
	
	private static void isImage(List<String> messages, MultipartFile file) {
        String fileName = file.getOriginalFilename();

        if (fileName != null) {
            String[] parts = fileName.split("\\.");
            if (parts.length > 0) {
                String extensao = parts[parts.length - 1].toLowerCase();
                if(!VALID_EXTENSIONS.contains(extensao))
                	messages.add("O arquivo fornecido não é do tipo imagem. Por favor, altere o arquivo e tente novamente.");
            }
        } else
        	messages.add("O campo IMAGEM não pode ser nulo.");
    }
	
}
